import axios from "axios";
import React, { useState } from "react";
import { Button, FormSelect, Form } from "react-bootstrap";
const Home = () => {
  const [food, setFood] = useState("");
  const [mealType, setMealType] = useState("");
  const getFoods = async () => {
    const APP_ID = process.env.REACT_APP_API_ID;
    const APP_KEY = process.env.REACT_APP_API_KEY;
    const url = `https://api.edamam.com/search?q=${food}&app_id=${APP_ID}&app_key=${APP_KEY}&mealType=${mealType}`;
    const { data } = await axios(url);
    console.log(data);
  };
  const handleClick = () => {
    getFoods();
  };

  return (
    <div>
      <Form
        onSubmit={handleClick}
        className="container w-50 d-flex g-2 border border-succes"
      >
        <input
          type="text"
          placeholder="food name"
          onChange={(e) => setFood(e.target.value)}
        />

        <FormSelect onChange={(e) => setMealType(e.target.value)}>
          <option value="breakfast">Breakfast</option>
          <option value="lunch">Lunch</option>
          <option value="dinner">Dinner</option>
          <option value="teatime">Tea Time</option>
          <option value="snack">Snack</option>
        </FormSelect>
        <Button onClick={handleClick}>Search</Button>
      </Form>
    </div>
  );
};
export default Home;
