import { useState } from "react";
import { Button, Form, FormSelect } from "react-bootstrap";
import axios from "axios";
const Home = () => {
  const [food, setFood] = useState("");
  const [mealType, setMealType] = useState("");

  const getFoods = async () => {
    const APP_ID = "b0b0e490";
    const APP_KEY = "72592519c63e69b7b9abab5ab598efb3";
    const url = `https://api.edamam.com/search?q=${food}&app_id=${APP_ID}&app_key=${APP_KEY}&mealType=${mealType}`;
    const { data } = await axios(url);
    console.log("data", data);
  };
  const handleClick = () => {
    getFoods();
  };
  return (
    <div>
      <Form
        onSubmit={handleClick}
        className=" container d-flex g-2 border border-success"
      >
        <input type="text" placeholder="food name" />
        <FormSelect onChange={(e) => setMealType(e.target.value)}>
          <option value="breakfast">Breakfast</option>
          <option value="lunch">Lunch</option>
          <option value="dinner">Dinner</option>
          <option value="tea time">Tea Time</option>
          <option value="snack">Snack</option>
        </FormSelect>
        <Button>Search</Button>
      </Form>
    </div>
  );
};

export default Home;
